import React from 'react';
import './css/imageSlide.css';

const ImageSlide = (props) => {

  const styles = {
    backgroundSize: 'cover',
    height:"100%",
    width:"100%",
    // overflow:"hidden",
    filter:`blur("4px")`
   };

  return(
    <div style={{position:"relative", height:"530px"}}>
      <img src={require('./images/the_fourth_phase.jpeg')} alt='moive cover background' style={styles} />
      <div style={{position:"absolute", top:0}}>
        <p> text </p>
      </div>
  </div>
  )
}

export default ImageSlide;
