import React ,{Component} from 'react';
import ImageSlide from './ImageSlide';
import Arrow from './Arrow';
import './css/carousel.css';

const data = [
  {
    image:require("./images/the_fourth_phase.jpeg"),
    title: "The Fourth Phase" ,
    description: "From the creators of Art of FLIGHT, Red Bull Media House presents THE FOURTH PHASE, a snowboarding epic starring athlete Travis Rice" ,
  }
  ]

class Carousel extends Component{
  constructor(props){
    super(props);
    this.state = {
      currentIndex: 0
    }
    this.nextSlide = this.nextSlide.bind(this);
    this.previousSlide = this.previousSlide.bind(this);
  }

  //for the left arrow
  previousSlide () {
   const lastIndex = data.length - 1;
   const { currentIndex } = this.state;
   const shouldResetIndex = currentIndex === 0;
   const index =  shouldResetIndex ? lastIndex : currentIndex - 1;

   this.setState({
     currentIndex: index
   });
 }

 //for the right arrow
 nextSlide () {
   const lastIndex = data.length - 1;
   const { currentIndex } = this.state;
   const shouldResetIndex = currentIndex === lastIndex;
   const index =  shouldResetIndex ? 0 : currentIndex + 1;

   this.setState({
     currentIndex: index
   });
 }

  render(){
    return (
      <div className="container">
        <Arrow clickFunction={this.previousSlide} direction="className='fas fa-angle-left'" className="left-arrow"  />
        <ImageSlide data={data[0]} />
        <Arrow clickFunction={this.nextSlide} direction="className=fas fa-angle-right" className="right-arrow" />
      </div>
    )
  }
}


export default Carousel;
